import React, { useMemo } from 'react';

const AnalysisSummary = ({ 
  artistA, 
  artistB, 
  analysisResults, 
  analysisMethod = 'trajectory',
  hoveredAxis 
}) => {
  
  // Î∂ÑÏÑù Í≤∞Í≥ºÎ≥Ñ ÏãúÍ∞ÅÌôî Îç∞Ïù¥ÌÑ∞
  const summaryData = useMemo(() => {
    if (!analysisResults) return null;

    const formatNumber = (num) => new Intl.NumberFormat('ko-KR').format(num);
    const formatCurrency = (num) => new Intl.NumberFormat('ko-KR', {
      style: 'currency',
      currency: 'KRW',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(num);

    return {
      trajectory: {
        title: 'Í∂§Ï†Å Î∂ÑÏÑù Í≤∞Í≥º',
        items: [
          {
            label: 'Ï¥ù Í∂§Ï†Å Ï∞®Ïù¥ ÏßÄÏàò',
            value: formatNumber(analysisResults.totalTrajectoryDifference.toFixed(1)),
            description: '4Í∞ú Ï∂ïÏùò ÏÑ±Ïû• Í≤ΩÎ°ú(AUC) Ï∞®Ïù¥Ïùò Ï¥ùÌï©',
            type: 'primary'
          },
          {
            label: 'Í∞ÄÏû• ÌÅ∞ Ï∞®Ïù¥ Ï∂ï',
            value: analysisResults.dominantDifferenceAxis?.axis || 'N/A',
            description: `Ï∞®Ïù¥: ${analysisResults.dominantDifferenceAxis?.difference?.toFixed(1) || 'N/A'}`,
            type: 'info'
          },
          {
            label: 'ÏÑ±Ïû• Ìå®ÌÑ¥ Ïú†ÏÇ¨ÎèÑ',
            value: `${(analysisResults.growthPatternSimilarity * 100).toFixed(1)}%`,
            description: 'Îëê ÏûëÍ∞ÄÏùò Ï†ÑÏ≤¥Ï†ÅÏù∏ ÏÑ±Ïû• Ìå®ÌÑ¥ ÏùºÏπòÎèÑ',
            type: 'secondary'
          }
        ]
      },
      market: {
        title: 'ÏãúÏû• Í∞ÄÏπò Î∂ÑÏÑù',
        items: [
          {
            label: 'ÏµúÍ≥† Ìò∏Îãπ Í∞ÄÍ≤© Ï∞®Ïù¥',
            value: formatCurrency(Math.abs(artistA.highestPricePerHo - artistB.highestPricePerHo)),
            description: `${artistA.name}: ${formatCurrency(artistA.highestPricePerHo)} | ${artistB.name}: ${formatCurrency(artistB.highestPricePerHo)}`,
            type: 'primary'
          },
          {
            label: 'ÏãúÏû• ÏÑ±ÏàôÎèÑ',
            value: analysisResults.marketMaturity?.leader || 'N/A',
            description: `ÏãúÏû• ÏÑ†ÎèÑ ÏûëÍ∞Ä (${analysisResults.marketMaturity?.score?.toFixed(2) || 'N/A'} Î∞∞ Ï∞®Ïù¥)`,
            type: 'info'
          },
          {
            label: 'Í∞ÄÍ≤© Î≥ÄÎèôÏÑ±',
            value: `${(analysisResults.priceVolatility * 100).toFixed(1)}%`,
            description: 'ÏµúÍ∑º 5ÎÖÑÍ∞Ñ ÌèâÍ∑† Í∞ÄÍ≤© Î≥ÄÎèôÎ•†',
            type: 'secondary'
          }
        ]
      },
      combined: {
        title: 'Ï¢ÖÌï© Î∂ÑÏÑù Í≤∞Í≥º',
        items: [
          {
            label: 'Í∂§Ï†Å-Í∞ÄÏπò ÏÉÅÍ¥ÄÍ≥ÑÏàò',
            value: analysisResults.trajectoryValueCorrelation?.toFixed(3) || 'N/A',
            description: 'Ïª§Î¶¨Ïñ¥ Í∂§Ï†ÅÍ≥º ÏãúÏû• Í∞ÄÏπò Í∞ÑÏùò ÏÉÅÍ¥ÄÍ¥ÄÍ≥Ñ',
            type: 'primary'
          },
          {
            label: 'ÎØ∏Îûò ÏÑ±Ïû• Ïû†Ïû¨Î†•',
            value: analysisResults.futurePotential?.leader || 'N/A',
            description: `ÏòàÏÉÅ ÏÑ±Ïû•Î•†: ${analysisResults.futurePotential?.growthRate?.toFixed(1) || 'N/A'}%`,
            type: 'success'
          },
          {
            label: 'Ìà¨Ïûê Î¶¨Ïä§ÌÅ¨ Î†àÎ≤®',
            value: analysisResults.riskLevel || 'N/A',
            description: 'Ìè¨Ìä∏Ìè¥Î¶¨Ïò§ Îã§Î≥ÄÌôî Í∂åÏû•ÎèÑ',
            type: analysisResults.riskLevel === 'High' ? 'warning' : 'info'
          }
        ]
      }
    };
  }, [analysisResults, artistA, artistB]);

  if (!summaryData || !analysisResults) {
    return (
      <div className="curator-chart-container curator-analysis-summary">
        <h3 className="curator-chart-title">üîç Î∂ÑÏÑù Í≤∞Í≥º</h3>
        <div className="curator-loading">
          <div className="curator-spinner"></div>
          <div>Î∂ÑÏÑù Í≤∞Í≥ºÎ•º Í≥ÑÏÇ∞ÌïòÍ≥† ÏûàÏäµÎãàÎã§...</div>
        </div>
      </div>
    );
  }

  const currentData = summaryData[analysisMethod];

  return (
    <div className="curator-chart-container curator-analysis-summary">
      <h3 className="curator-chart-title">
        üîç {currentData.title}
        {hoveredAxis && (
          <span className="current-focus">
            ‚Ä¢ ÌòÑÏû¨ Ìè¨Ïª§Ïä§: {hoveredAxis} Ï∂ï
          </span>
        )}
      </h3>
      
      <div className="analysis-summary-grid">
        {currentData.items.map((item, index) => (
          <div 
            key={index}
            className={`summary-card summary-card--${item.type} curator-animate-fade-in curator-animate-delay-${100 + (index * 50)}`}
          >
            <div className="summary-header">
              <span className="summary-label">{item.label}</span>
              <div className={`summary-indicator summary-indicator--${item.type}`}></div>
            </div>
            
            <div className="summary-value">{item.value}</div>
            
            <div className="summary-description">
              {item.description}
            </div>
          </div>
        ))}
      </div>

      {/* ÏÉÅÏÑ∏ Ïù∏ÏÇ¨Ïù¥Ìä∏ ÏÑπÏÖò */}
      <div className="analysis-insights">
        <h4 className="insights-title">üí° ÌïµÏã¨ Ïù∏ÏÇ¨Ïù¥Ìä∏</h4>
        <div className="insights-content">
          {generateInsights(analysisResults, artistA, artistB, analysisMethod)}
        </div>
      </div>

      {/* Ï∂ïÎ≥Ñ ÏÑ∏Î∂Ä Î∂ÑÏÑù (Ìò∏Î≤ÑÏãú ÌëúÏãú) */}
      {hoveredAxis && analysisResults.axisDetails && (
