import React, { useState, useEffect, useCallback } from 'react';
import aiService from '../../services/aiService';
import aiCache from '../../utils/ai/aiCache';

const AIInsightsPanel = ({ artistData, phase = 1, className = '' }) => {
  const [insights, setInsights] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState(null);

  // AI Î∂ÑÏÑù Ïã§Ìñâ
  const generateInsights = useCallback(async (forceRefresh = false) => {
    if (!artistData || !artistData.name) {
      setError('Î∂ÑÏÑùÌï† ÏïÑÌã∞Ïä§Ìä∏ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Ï∫êÏãú ÌôïÏù∏ (forceRefreshÍ∞Ä falseÏù∏ Í≤ΩÏö∞)
      if (!forceRefresh) {
        const cacheKey = aiCache.generateCacheKey(artistData, phase);
        const cachedResult = aiCache.get(cacheKey);
        
        if (cachedResult) {
          console.log('üóÑÔ∏è Ï∫êÏãúÎêú AI Î∂ÑÏÑù Í≤∞Í≥º ÏÇ¨Ïö©');
          setInsights(cachedResult);
          setLoading(false);
          return;
        }
      }

      // AI Î∂ÑÏÑù Ïã§Ìñâ
      console.log('ü§ñ ÏÉàÎ°úÏö¥ AI Î∂ÑÏÑù ÏãúÏûë:', artistData.name);
      const result = await aiService.generatePhase1Insights(artistData);

      if (result.success) {
        setInsights(result);
        
        // Ï∫êÏãúÏóê Ï†ÄÏû• (Ìè¥Î∞±Ïù¥ ÏïÑÎãå Í≤ΩÏö∞)
        if (!result.fallbackUsed) {
          const cacheKey = aiCache.generateCacheKey(artistData, phase);
          aiCache.set(cacheKey, result);
        }
      } else {
        throw new Error(result.error || 'AI Î∂ÑÏÑùÏùÑ ÏôÑÎ£åÌï† Ïàò ÏóÜÏäµÎãàÎã§.');
      }

    } catch (err) {
      console.error('AI Î∂ÑÏÑù Ïò§Î•ò:', err);
      setError(getErrorMessage(err));
    } finally {
      setLoading(false);
    }
  }, [artistData, phase]);

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú ÏûêÎèô Î∂ÑÏÑù Ïã§Ìñâ
  useEffect(() => {
    generateInsights();
  }, [generateInsights]);

  // API Ïó∞Í≤∞ ÏÉÅÌÉú ÌôïÏù∏
  useEffect(() => {
    const checkConnection = async () => {
      const status = await aiService.checkConnection();
      setConnectionStatus(status);
    };
    checkConnection();
  }, []);

  // ÏóêÎü¨ Î©îÏãúÏßÄ Ï≤òÎ¶¨
  const getErrorMessage = (error) => {
    if (error.message?.includes('quota')) {
      return 'ÏùºÏùº AI Î∂ÑÏÑù ÌïúÎèÑÏóê ÎèÑÎã¨ÌñàÏäµÎãàÎã§. ÎÇ¥Ïùº Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.';
    } else if (error.message?.includes('network')) {
      return 'ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏ÌïòÍ≥† Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.';
    } else if (error.message?.includes('timeout')) {
      return 'AI Î∂ÑÏÑù ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.';
    }
    return 'AI Î∂ÑÏÑù Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.';
  };

  // ÏÉàÎ°úÍ≥†Ïπ® Ìï∏Îì§Îü¨
  const handleRefresh = () => {
    generateInsights(true); // Ï∫êÏãú Î¨¥ÏãúÌïòÍ≥† ÏÉàÎ°ú Î∂ÑÏÑù
  };

  // Î†åÎçîÎßÅ Ï°∞Í±¥Î∂Ä Ï≤òÎ¶¨
  if (loading) {
    return (
      <div className={`curator-chart-container ${className}`}>
        <div className="curator-chart-title">
          ü§ñ AI Î∂ÑÏÑù Ïù∏ÏÇ¨Ïù¥Ìä∏
        </div>
        <div className="curator-loading">
          <div className="curator-spinner"></div>
          <div className="curator-loading-text">
            AIÍ∞Ä {artistData?.name || 'ÏûëÍ∞Ä'}Ïùò Îç∞Ïù¥ÌÑ∞Î•º Î∂ÑÏÑùÌïòÍ≥† ÏûàÏäµÎãàÎã§...
          </div>
          <div className="ai-loading-details">
            <p>‚Ä¢ 5Ï∂ï Î†àÏù¥Îçî Îç∞Ïù¥ÌÑ∞ Ìï¥ÏÑù Ï§ë</p>
            <p>‚Ä¢ 4Ï∂ï ÏÑ†Î≤ÑÏä§Ìä∏ Í∑ºÍ±∞ Î∂ÑÏÑù Ï§ë</p>
            <p>‚Ä¢ ÏãúÏû• Ìè¨ÏßÄÏÖîÎãù ÌèâÍ∞Ä Ï§ë</p>
          </div>
        </div>
      </div>
    );
  }

  if (error && !insights) {
    return (
      <div className={`curator-chart-container ${className}`}>
        <div className="curator-chart-title">
          ü§ñ AI Î∂ÑÏÑù Ïù∏ÏÇ¨Ïù¥Ìä∏
        </div>
        <div className="curator-error">
          <div className="curator-error-icon">ü§ñ‚ùå</div>
          <h3 className="curator-error-title">AI Î∂ÑÏÑù Ïò§Î•ò</h3>
          <p className="curator-error-message">{error}</p>
          <button 
            className="ai-retry-button"
            onClick={handleRefresh}
          >
            üîÑ Îã§Ïãú ÏãúÎèÑ
          </button>
        </div>
        {!connectionStatus?.connected && (
          <div className="ai-connection-warning">
            ‚ö†Ô∏è API Ïó∞Í≤∞ ÏÉÅÌÉú: {connectionStatus?.reason || 'ÌôïÏù∏ Ï§ë...'}
          </div>
        )}
      </div>
    );
  }

  return (
    <div className={`curator-chart-container ai-insights-panel ${className}`}>
      {/* Ìó§Îçî */}
      <div className="ai-insights-header">
        <div className="curator-chart-title">
          ü§ñ AI Î∂ÑÏÑù Ïù∏ÏÇ¨Ïù¥Ìä∏
        </div>
        <div className="ai-insights-controls">
          <button
            className="ai-refresh-button"
            onClick={handleRefresh}
            disabled={loading}
            title="ÏÉàÎ°úÏö¥ Î∂ÑÏÑù ÏÉùÏÑ±"
          >
            üîÑ
          </button>
          <div className="ai-model-badge">
            {insights?.model === 'fallback' ? 'üìä ÌÜµÍ≥ÑÎ∂ÑÏÑù' : 
             insights?.model === 'gpt-4' ? 'üß† GPT-4' : 
             insights?.model === 'claude-3' ? 'üé≠ Claude-3' : 'ü§ñ AI'}
          </div>
        </div>
      </div>

      {/* Î∂ÑÏÑù Í≤∞Í≥º */}
      <div className="ai-insights-content">
        {insights?.insights ? (
          <div className="ai-insights-text">
            {formatInsights(insights.insights)}
          </div>
        ) : (
          <div className="ai-insights-placeholder">
            Î∂ÑÏÑù Í≤∞Í≥ºÎ•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...
          </div>
        )}
      </div>

      {/* Î©îÌÉÄ Ï†ïÎ≥¥ */}
      <div className="ai-insights-footer">
        <div className="ai-insights-meta">
          <span className="ai-timestamp">
            üìÖ {insights?.timestamp ? new Date(insights.timestamp).toLocaleString('ko-KR') : ''}
          </span>
          {insights?.fallbackUsed && (
            <span className="ai-fallback-badge">
              üîÑ ÌÜµÍ≥Ñ Í∏∞Î∞ò Î∂ÑÏÑù
            </span>
          )}
          {insights?.statistics && (
            <span className="ai-stats-badge" title={`ÌèâÍ∑†: ${insights.statistics.average}, Î∂ÑÏÇ∞: ${insights.statistics.variance}`}>
              üìà Îç∞Ïù¥ÌÑ∞ ÌÜµÍ≥Ñ
            </span>
          )}
        </div>
        
        {error && (
          <div className="ai-error-notice">
            ‚ö†Ô∏è {error}
          </div>
        )}
      </div>
    </div>
  );
};

// Ïù∏ÏÇ¨Ïù¥Ìä∏ ÌÖçÏä§Ìä∏ Ìè¨Îß∑ÌåÖ (ÎßàÌÅ¨Îã§Ïö¥ Ïä§ÌÉÄÏùº)
const formatInsights = (insightsText) => {
  if (!insightsText) return null;

  // Í∞ÑÎã®Ìïú ÎßàÌÅ¨Îã§Ïö¥ ÌååÏã±
  const lines = insightsText.split('\n');
  
  return lines.map((line, index) => {
    // Ìó§Îî© Ï≤òÎ¶¨
    if (line.startsWith('###')) {
      return <h4 key={index} className="ai-insight-heading-3">{line.replace('###', '').trim()}</h4>;
    } else if (line.startsWith('##')) {
      return <h3 key={index} className="ai-insight-heading-2">{line.replace('##', '').trim()}</h3>;
    } else if (line.startsWith('#')) {
      return <h2 key={index} className="ai-insight-heading-1">{line.replace('#', '').trim()}</h2>;
    }
    
    // Îπà Ï§Ñ Ï≤òÎ¶¨
    if (line.trim() === '') {
      return <br key={index} />;
    }
    
    // ÏùºÎ∞ò ÌÖçÏä§Ìä∏ (ÍµµÏùÄ Í∏ÄÏî® Ï≤òÎ¶¨)
    const formattedLine = line.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
    
    return (
      <p 
        key={index} 
        className="ai-insight-paragraph"
        dangerouslySetInnerHTML={{ __html: formattedLine }}
      />
    );
  });
};

export default AIInsightsPanel;
