        await new Promise(resolve => setTimeout(resolve, 1000));
        
        setTimeseriesData(mockTimeseriesData);
        setCareerEvents(mockCareerEvents);

        // Îç∞Ïù¥ÌÑ∞ Ï≤òÎ¶¨ ÌååÏù¥ÌîÑÎùºÏù∏ Ïã§Ìñâ
        const processed = processTimeseriesData(
          mockTimeseriesData, 
