import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/layout/Header';
import ArtistPhase1View from './components/layout/ArtistPhase1View';
import ArtistPhase2View from './components/layout/ArtistPhase2View';
import ErrorBoundary from './components/common/ErrorBoundary';
import './styles/globals.css';
import './styles/components.css';

function App() {
  // Phase 전환 상태 관리 (Dr. Sarah Kim's Interactive State Design)
  const [currentPhase, setCurrentPhase] = useState(1);
  const [currentArtistId, setCurrentArtistId] = useState("ARTIST_0005");

  // Phase 1 → 2 드릴다운 핸들러
  const handleDrilldownToPhase2 = (artistId = currentArtistId) => {
    setCurrentArtistId(artistId);
    setCurrentPhase(2);
    
    // 부드러운 전환 효과를 위한 스크롤
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Phase 2 → 1 복귀 핸들러
  const handleBackToPhase1 = () => {
    setCurrentPhase(1);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <ErrorBoundary>
      <Router>
        <div className="App">
          <Header />
          <main>
            <Routes>
              <Route 
                path="/" 
                element={
                  currentPhase === 1 ? (
                    <ArtistPhase1View 
                      artistId={currentArtistId}
                      onDrilldownToPhase2={handleDrilldownToPhase2}
                    />
                  ) : (
                    <ArtistPhase2View 
                      artistId={currentArtistId}
                      onBackToPhase1={handleBackToPhase1}
                    />
                  )
                }
              />
              <Route 
                path="/artist/:id" 
                element={
                  currentPhase === 1 ? (
                    <ArtistPhase1View 
                      onDrilldownToPhase2={handleDrilldownToPhase2}
                    />
                  ) : (
                    <ArtistPhase2View 
                      onBackToPhase1={handleBackToPhase1}
                    />
                  )
                }
              />
              {/* Phase 2 직접 접근 라우트 */}
              <Route 
                path="/artist/:id/trajectory"
                element={
                  <ArtistPhase2View 
                    onBackToPhase1={handleBackToPhase1}
                  />
                }
              />
              {/* 향후 Phase 3 추가 예정 */}
            </Routes>
          </main>
        </div>
      </Router>
    </ErrorBoundary>
  );
}

export default App;
